import {useState} from 'react'

function Square({state, clickFunc}) {
  return <button className="square" onClick={clickFunc}>{state}</button>
}

export default function Board() {
  const [squares, setSquares] = useState(Array(9).fill(null))
  const [isXNext, setIsXNext] = useState(true)
  let gameHistory = []
  console.log(squares, gameHistory)

  let gameStatus = isXNext ? 'Player X\'s turn.' : 'Player O\'s turn.'
  if(calculateWinner(squares))
    gameStatus = isXNext ? 'Winner is O.' : 'Winner is X.'

  const whenBtnClicked = (ind) => {
    if (squares[ind] || calculateWinner(squares))
      return

    const newSquares = [...squares]
    gameHistory.push(newSquares)

    if (isXNext)
      newSquares[ind] = 'X'
    else
      newSquares[ind] = 'O'

    setSquares(newSquares)
    setIsXNext(!isXNext)
  }

  return (
    <>
      <div className='status'>{gameStatus}</div>
      <div className='board-row'>
        <Square state={squares[0]} clickFunc={() => whenBtnClicked(0)} />
        <Square state={squares[1]} clickFunc={() => whenBtnClicked(1)} />
        <Square state={squares[2]} clickFunc={() => whenBtnClicked(2)} />
      </div>
      <div className='board-row'>
        <Square state={squares[3]} clickFunc={() => whenBtnClicked(3)} />
        <Square state={squares[4]} clickFunc={() => whenBtnClicked(4)} />
        <Square state={squares[5]} clickFunc={() => whenBtnClicked(5)} />
      </div>
      <div className='board-row'>
        <Square state={squares[6]} clickFunc={() => whenBtnClicked(6)} />
        <Square state={squares[7]} clickFunc={() => whenBtnClicked(7)} />
        <Square state={squares[8]} clickFunc={() => whenBtnClicked(8)} />
      </div>
      <div className='game-info'>
        <ol>
          <button>Go to game start.</button>
          {gameHistory.map((ele, ind) => {
            const btnText = `Go to move: ${ind}`
            return <li><button>{btnText}</button></li>
          })}
        </ol>
      </div>
    </>
  )
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}
