import {useState} from 'react'

function Row() {
  const [state, setState] = useState([null, null, null])

  const whenBtnClicked = (ind) => {
    const newState = [...state]
    newState[ind] = 'X'
    setState(newState)
  }

  const rowElements = [
    <button className="square" onClick={() => whenBtnClicked(0)}>{state[0]}</button>,
    <button className="square" onClick={() => whenBtnClicked(1)}>{state[1]}</button>,
    <button className="square" onClick={() => whenBtnClicked(2)}>{state[2]}</button>
  ]

  return <div className='board-row'>{rowElements}</div>
}

export default function Square() {
  return (
    <div>
      <Row />
      <Row />
      <Row />
    </div>
  )
}
